&lt;script type=&quot;text/javascript&quot;&gt;
    function readURL(input, id) {
        if (input.files &amp;&amp; input.files[0]) {
            var reader = new FileReader();

            reader.onload = function(e) {
                $(id).attr(&#039;src&#039;, e.target.result);
            }

            reader.readAsDataURL(input.files[0]); // convert to base64 string
        }
    }

    $(&quot;#imgInp&quot;).change(function() {
        readURL(this, &#039;#blah&#039;);
    });

    $(&quot;#img&quot;).change(function() {
        readURL(this, &#039;#blahE&#039;);
    });
&lt;/script&gt;
&lt;script type=&quot;text/javascript&quot;&gt;
    var read_buku = {
        limit: 50,
        page: 1
    }

    function refresh_buku() {
        $.ajax({
                url: &#039;http://kitakeren-web.test/index.php/master/read_dashboard/show/dashboard/pagination&#039;,
                type: &#039;GET&#039;,
                dataType: &#039;json&#039;,
                data: read_buku,
            })
            .done(function(data) {
                if (read_buku.page == 1) {
                    $(&quot;#filter-previous_page&quot;).addClass(&#039;disabled&#039;);
                } else {
                    $(&quot;#filter-previous_page&quot;).removeClass(&#039;disabled&#039;);
                }
                if (read_buku.page == data.total_page) {
                    $(&quot;#filter-next_page&quot;).addClass(&#039;disabled&#039;);
                } else {
                    $(&quot;#filter-next_page&quot;).removeClass(&#039;disabled&#039;);
                }

                if (data.total_data &lt; data.limit) {
                    $(&quot;#filter-limit&quot;).attr(&#039;value&#039;, data.total_data);
                    $(&quot;#filter-limit&quot;).attr(&#039;max&#039;, data.total_data);
                }
                $(&quot;#filter-page&quot;).attr(&#039;max&#039;, data.total_page);
                $(&quot;#dtl&quot;).attr(&#039;data-title&#039;, &#039;Halaman Maksimum: &#039; + data.total_page);
                // $(&quot;#filter-page&quot;).attr(&#039;title&#039;, &#039;Halaman Maksimum: &#039; + data.total_page);
                $(&quot;#filter-page&quot;).attr(&#039;data-original-title&#039;, &#039;Halaman Maksimum: &#039; + data.total_page);
                $.ajax({
                        url: &#039;http://kitakeren-web.test/index.php/master/read_dashboard/show/dashboard&#039;,
                        type: &#039;GET&#039;,
                        dataType: &#039;html&#039;,
                        data: read_buku,
                    })
                    .done(function(data) {
                        $(&quot;#data-load&quot;).fadeOut(100, function() {
                            $(&quot;#data-load&quot;).html(data).fadeIn(100);
                            event_buku();
                        });
                    });
            });
    }

    function refresh_detail_dashboard() {
        $.ajax({
                url: &#039;http://kitakeren-web.test/index.php/master/read_detail_dashboard/show/detail_dashboard/pagination&#039;,
                type: &#039;GET&#039;,
                dataType: &#039;json&#039;,
                data: read_buku,
            })
            .done(function(data) {
                if (read_buku.page == 1) {
                    $(&quot;#filter-previous_page&quot;).addClass(&#039;disabled&#039;);
                } else {
                    $(&quot;#filter-previous_page&quot;).removeClass(&#039;disabled&#039;);
                }
                if (read_buku.page == data.total_page) {
                    $(&quot;#filter-next_page&quot;).addClass(&#039;disabled&#039;);
                } else {
                    $(&quot;#filter-next_page&quot;).removeClass(&#039;disabled&#039;);
                }

                if (data.total_data &lt; data.limit) {
                    $(&quot;#filter-limit&quot;).attr(&#039;value&#039;, data.total_data);
                    $(&quot;#filter-limit&quot;).attr(&#039;max&#039;, data.total_data);
                }
                $(&quot;#filter-page&quot;).attr(&#039;max&#039;, data.total_page);
                $(&quot;#dtl&quot;).attr(&#039;data-title&#039;, &#039;Halaman Maksimum: &#039; + data.total_page);
                // $(&quot;#filter-page&quot;).attr(&#039;title&#039;, &#039;Halaman Maksimum: &#039; + data.total_page);
                $(&quot;#filter-page&quot;).attr(&#039;data-original-title&#039;, &#039;Halaman Maksimum: &#039; + data.total_page);
                $.ajax({
                        url: &#039;http://kitakeren-web.test/index.php/master/read_detail_dashboard/show/detail_dashboard&#039;,
                        type: &#039;GET&#039;,
                        dataType: &#039;html&#039;,
                        data: read_buku,
                    })
                    .done(function(data) {
                        $(&quot;#data-load&quot;).fadeOut(100, function() {
                            $(&quot;#data-load&quot;).html(data).fadeIn(100);
                        });
                    });
            });
    }

    refresh_buku();

    // ----------------------------------------
    // Event reload
    // ----------------------------------------
    $(&quot;#reload&quot;).click(function(e) {
        refresh_buku();
    });

    // -------------------------------------
    // Event setelah data buku dimuat
    // -------------------------------------
    function event_buku() {
        // ----------------------------------------
        // Event klik edit buku
        // ----------------------------------------
        ctx_modal_edit_buku = $(&quot;#modal-edit&quot;);
        $(&quot;.edit-data&quot;).click(function(e) {
            id = $(this).data(&#039;id&#039;);
            $.ajax({
                    url: &#039;http://kitakeren-web.test/index.php/master/read_dashboard/single&#039;,
                    type: &#039;GET&#039;,
                    dataType: &#039;json&#039;,
                    data: {
                        id: id
                    },
                })
                .done(function(data) {
                    $(&quot;#form-edit input[name=&#039;id_dashboard&#039;]&quot;).val(data[0].id_dashboard);
                    $(&quot;#form-edit input[name=&#039;ind&#039;]&quot;).val(data[0].nama_dashboard.ind);
                    $(&quot;#form-edit input[name=&#039;en&#039;]&quot;).val(data[0].nama_dashboard.en);
                    $(&quot;#form-edit img[id=&#039;blahE&#039;]&quot;).attr(&#039;src&#039;, &#039;/&#039; + data[0].icon_dashboard);
                    ctx_modal_edit_buku.modal(&#039;show&#039;).on(&#039;shown.bs.modal&#039;, function(e) {
                        $(&quot;#form-edit input[name=&#039;ind&#039;]&quot;).focus();
                    });
                });
        });

        // ----------------------------------------
        // Hapus Data Buku
        // ----------------------------------------
        $(&quot;.hapus-data&quot;).click(function(e) {
            e.preventDefault();
            id = $(this).data(&#039;id&#039;);
            if (confirm(&quot;Anda yakin?&quot;)) {
                $.ajax({
                        url: &#039;http://kitakeren-web.test/index.php/master/write_dashboard/delete&#039;,
                        type: &#039;POST&#039;,
                        dataType: &#039;json&#039;,
                        data: {
                            id: id
                        },
                    })
                    .done(function(data) {
                        if (data) {
                            refresh_buku();
                            buat_notifikasi({
                                icon: &#039;done_outline&#039;,
                                message: &quot;Data berhasil dihapus&quot;,
                                type: &#039;success&#039;
                            });
                        }
                    });
            }
        });

        // ----------------------------------------
        // Detail Data Buku
        // ----------------------------------------
        $(&quot;.detail-data&quot;).click(function(e) {
            e.preventDefault();
            id = $(this).data(&#039;id&#039;);
            console.log(id);
            $(&#039;.content div&#039;).remove();
            $(&#039;.content&#039;).load(&#039;show_view/1/&#039;+id);
        });

        // ----------------------------------------
        // Reset Gambar
        // ----------------------------------------
        $(&quot;.bt-close&quot;).click(function(e) {
            e.preventDefault();
            $(&quot;.img-def&quot;).attr(&#039;src&#039;, &#039;/assets/transparant.png&#039;);
        });
    }

    // -------------------------------------
    // Event Filter / Penyaringan Data
    // -------------------------------------
    var ctx_limit = $(&quot;#filter-limit&quot;);
    var ctx_page = $(&quot;#filter-page&quot;);
    var ctx_next_page = $(&quot;#filter-next_page&quot;);
    var ctx_previous_page = $(&quot;#filter-previous_page&quot;);
    var ctx_cari = $(&quot;#filter-cari&quot;);

    ctx_limit.on(&#039;input&#039;, function(e) {
        value = $(this).val();
        if (value &gt; 0) {
            read_buku.limit = value;
        } else {
            $(this).val(50);
        }
        $(&quot;#filter-page&quot;).val(1);
        read_buku.page = 1;
        refresh_buku();
    });
    ctx_page.on(&#039;input&#039;, function(e) {
        value = $(this).val();
        if (value &gt; 0) {
            read_buku.page = value;
        } else {
            $(this).val(1);
        }
        refresh_buku();
    });
    ctx_next_page.click(function(e) {
        disabled = $(this).hasClass(&#039;disabled&#039;);
        if (!disabled) {
            read_buku.page++;
            ctx_page.val(read_buku.page);
            refresh_buku();
        }
    });
    ctx_previous_page.click(function(e) {
        disabled = $(this).hasClass(&#039;disabled&#039;);
        if (!disabled) {
            read_buku.page--;
            ctx_page.val(read_buku.page);
            refresh_buku();
        }
    });
